/*
* Prefixed by https://autoprefixer.github.io
* PostCSS: v8.3.6,
* Autoprefixer: v10.3.1
* Browsers: last 4 version
*/

.footer 
{

    margin-top: 16rem;
    padding: $desktop-padding;
    height: auto;
    background: #000;
    &__grid 
    {

        height: auto;
        display: -ms-grid;
        display: grid;
        -ms-grid-columns: 1fr 60px 1fr 60px 1fr 60px 1fr;
        grid-template-columns: 1fr 1fr 1fr 1fr;
        -ms-grid-rows: auto 60px auto 60px auto;
            grid-template-areas: 'community community community sone' 'b1 b2 b3 stwo' 'linksleft linksleft linksleft linksright';
        gap: 60px;
        padding: 5rem 0;

        .community, .showcase-one, .showcase-two, .links, .bottom-left, .bottom-right , .box-one, .box-two, .box-three
        {
            width: 100%;
            height: auto;


        }

        .community 
        {
            -ms-grid-row: 1;
            -ms-grid-column: 1;
            -ms-grid-column-span: 5;
            grid-area: community; 
            height: 200px;
            width: 100%;
            background: transparent;
            -ms-flex-wrap: wrap;
                flex-wrap: wrap;
            padding: 1rem;

            span 
            {
                font-size: 1rem;
                display: block;
                width: 30%;
                font-weight: bold;
                color: #fff;
            }

            h2 
            {
                font-size: 8rem;
                line-height: 9rem;
                text-align: left;
                margin-top: 3rem;
                color: #fff;

            }
        }

        .showcase-one 
        {
            -ms-grid-row: 1;
            -ms-grid-column: 7;
            grid-area: sone; 
            height: 300px;

            background-image: url('/css/resources/product-one.jpg');
            background-size: cover;
            background-position: center center;
        }

        .showcase-two 
        {
            -ms-grid-row: 3;
            -ms-grid-column: 7;
            grid-area: stwo; 
            height: 300px;

            background-image: url('/css/resources/product-two.jpg');
            background-size: cover;
            background-position: center center;
        }

        .box-one 
        {
            -ms-grid-row: 3;
            -ms-grid-column: 1;
            grid-area: b1;
            height: 300px;
            @include flexbox(end, start);
            -webkit-box-orient: vertical;
            -webkit-box-direction: normal;
                -ms-flex-direction: column;
                    flex-direction: column;
            color: #fff;
            padding: 0 2rem;
            p 
            {
                font-size: 2rem;
                font-weight: lighter;
            }

            .social 
            {
                margin-top: 1rem;
            }

            a 
            {
                @include resetLinks;
                color: #fff;
                padding: 0 5px;
                padding-top: 1rem;
            }
        }

        .box-two
        {
            -ms-grid-row: 3;
            -ms-grid-column: 3;
            grid-area: b2;
            height: 350px;
            @include flexbox(center, center);
            width: 100%;


            form 
            {
                padding: 0 2rem;
                width: 100%;
                input 
                {
                    display: block;
                    width: 100%;
                    border: none;
                    background: transparent;
                    padding: 8px 0; 
                    font-size: 1.5rem;
                    outline: none;
                    color: #fff;
                    border-bottom: 2px solid $primary-clr;
                }
                button 
                {
                    margin-top: 2rem;
                    width: 100%;
                    background: #fff;
                    border:  none;
                    padding: 15px 0;
                    font-weight: bold;
                    font-size: 1rem;
                    border-radius: 5px;
                    cursor: pointer;
                    &:hover 
                    {
                        background: $primary-clr;
                        color: #fff;
                    }
                }
            }
        }

        .box-three
        {
            -ms-grid-row: 3;
            -ms-grid-column: 5;
            grid-area: b3;
            height: 300px;
            @include flexbox(end, start);
            -webkit-box-orient: vertical;
            -webkit-box-direction: normal;
                -ms-flex-direction: column;
                    flex-direction: column;
            padding: 0 2rem;
            a 
            {
                display: block;
                @include resetLinks;
                color: #fff;
                padding: 15px 0;
                font-size: 1.5rem;
                text-align: right;
                position: relative;
                -webkit-transition: all .3s ease-in-out;
                -o-transition: all .3s ease-in-out;
                transition: all .3s ease-in-out;
            }
        }
        .bottom-left 
        {
            -ms-grid-row: 5;
            -ms-grid-column: 1;
            -ms-grid-column-span: 5;
            grid-area: linksleft;
            height: 50px;
            @include flexbox(start, center);

            a 
            {
                @include resetLinks;
                font-size: 1.2rem;
                margin: 0 15px;
                color: #ccc;
                position: relative;
                -webkit-transition: all .3s ease-in-out;
                -o-transition: all .3s ease-in-out;
                transition: all .3s ease-in-out;
            }
        }

        .bottom-right 
        {
            -ms-grid-row: 5;
            -ms-grid-column: 7;
            grid-area: linksright;
            height:  50px;

            @include flexbox(end, center);

            a 
            {
                @include resetLinks;
                font-size: 1.2rem;

                color: #ccc;
                position: relative;
                -webkit-transition: all .3s ease-in-out;
                -o-transition: all .3s ease-in-out;
                transition: all .3s ease-in-out;
            }
        }
    }
}


