.preload-anime 
{
    opacity: 0;
}

.cursor 
{
    position: fixed;
    opacity: 0;
    pointer-events: none;
    width: 50px;
    height: 50px;
    border-radius: 50%;
    background-color: $primary-clr;
    -webkit-transition: -webkit-transform 750ms ease;
    transition: -webkit-transform 750ms ease;
    -o-transition: transform 750ms ease;
    transition: transform 750ms ease;
    transition: transform 750ms ease, -webkit-transform 750ms ease;;
    mix-blend-mode: difference;
    -webkit-transform: translate(-50%, -50%) scale(.3);
        -ms-transform: translate(-50%, -50%) scale(.3);
            transform: translate(-50%, -50%) scale(.3);
    z-index: 50;
    -webkit-clip-path: circle(50% at 50% 50%);
            clip-path: circle(50% at 50% 50%);

}

.custom-cursor--link 
{
    -webkit-transform: translate(-50%, -50%) scale(1);
        -ms-transform: translate(-50%, -50%) scale(1);
            transform: translate(-50%, -50%) scale(1);
    background-image: url('/css/resources/test.svg');
    width: 120px;
    height: 120px;
    background-size: cover;
    background-position: center center;
}

.custom-cursor--link-nav 
{
    -webkit-transform: translate(-50%, -50%) scale(1);
        -ms-transform: translate(-50%, -50%) scale(1);
            transform: translate(-50%, -50%) scale(1);

}

// Fixed elements

.preloader 
{
    position: fixed;
    top: 0;
    left: 0;
    background: $primary-clr;
    z-index: 50;
    width: 100%;
    height: 100%;
    z-index: 100;
    &__text
    {
        position: absolute;
        top: 50%;
        left: 50%;
        -webkit-transform: translate(-50%, -50%);
            -ms-transform: translate(-50%, -50%);
                transform: translate(-50%, -50%);
        font-size: 5rem;
        color: #fff;
    }
}


.cart-added-item 
{
    position: fixed;
    bottom: 0;
   right: 0;
    background: #fff;
    z-index: 20;
    padding: 20px;
    font-size: 1.5rem;
    opacity: 0;
    visibility: hidden;;
}
.cart-results-modal 
{
    width: 50%;
    height: 100%;
