*/

// Main Content 

main 
{

    // Products and stories share similar layout

    .products 
    {
        margin-bottom: -5rem;
        &__heading 
        {
            @include flexbox(space-between, center);
            margin-bottom: 8rem;

            span 
            {
                font-size: 66px;
                line-height: 77px;
            }

            a 
            {
                @include resetLinks;
                color: $primary-clr;
                font-weight: bold;
                @include flexbox(space-between, center);
                width: 300px;
                position: relative;
                -webkit-transition: all .3s ease-in-out;
                -o-transition: all .3s ease-in-out;
                transition: all .3s ease-in-out;
                &:before 
                {
                    content: ''; 
                    position: absolute;
                    bottom: -8px;
                    left: 0;
                    right: 0;
                    width: 0;
                    height: 2px;
                    background: $primary-clr;
                    -webkit-transition: all .3s ease-in-out;
                    -o-transition: all .3s ease-in-out;
                    transition: all .3s ease-in-out;
                }


                &:after
                {
                    content: ''; 
                    position: absolute;
                    bottom: -8px;
                    right: 0;
                    width: 0;
                    height: 2px;
                    background: $primary-clr;
                    -webkit-transition: all .3s ease-in-out;
                    -o-transition: all .3s ease-in-out;
                    transition: all .3s ease-in-out;
                }
                &:hover:before ,
                &:hover:after
                {
                    width: 50%;
                    -webkit-transition: all .3s ease-in-out;
                    -o-transition: all .3s ease-in-out;
                    transition: all .3s ease-in-out;
                }
            }
        }
        margin-top: 8rem;
        padding: $desktop-padding;
        &__container 
        {   
            width: 100%;
            height: 100%;
            @include flexbox(start, start);

            &__box 
            {
                width: 100%;
                height: 100%;

                img 
                {
                    width: 100%;
                    height: 100%;
                    position: relative;
                    overflow: hidden;
                    -webkit-transition: 0.3s ease-in-out;
                    -o-transition: 0.3s ease-in-out;
                    transition: 0.3s ease-in-out;
                }

                .prod-showcase 
                {
                    overflow: hidden; 
                    -webkit-transition: 0.3s ease-in-out; 
                    -o-transition: 0.3s ease-in-out; 
                    transition: 0.3s ease-in-out;
