                    -o-transition: all .3s ease-in-out;
                    transition: all .3s ease-in-out;
                    @include resetLinks;
                    color: $primary-clr;


                    i 
                    {
                        margin-left: .5rem;
                    }
                    &:before 
                    {
                        content: ''; 
